<script>
  function initMap() {
    // Initialize the map
    const map = new google.maps.Map(document.getElementById('map'), {
      center: { lat: -34.397, lng: 150.644 }, // Set initial map center
      zoom: 8, // Set initial zoom level
    });

    // Add markers to the map
    const marker = new google.maps.Marker({
      position: { lat: -34.397, lng: 150.644 }, // Set marker position
      map: map,
      title: 'Marker Title', // Set marker title
    });

    // Add info window to the marker (hover effect)
    const infowindow = new google.maps.InfoWindow({
      content: 'Marker Content', // Set info window content
    });

    // Add mouseover event to the marker
    marker.addListener('mouseover', function () {
      infowindow.open(map, marker);
    });

    // Add mouseout event to the marker
    marker.addListener('mouseout', function () {
      infowindow.close();
    });
  }
</script>
